import './App.css';
import React, {useEffect, useState} from 'react';
import axios from 'axios';
import styled from "styled-components";
import * as ReactBootStrap from 'react-bootstrap';

axios.defaults.headers.post['Access-Control-Allow-Origin'] = '*';

const App = () => {
  const [apiResponse, setApiResponse] = useState({
    match_table : '',
    f1_score : 0
  })
  const [loading, setLoading] = useState(false)

  const apiCall = async () => {
    try {
      axios.get('http://localhost:5000/predict').then(response => {
      setApiResponse({
        match_table : response.data.return,
        f1_score : response.data.F1_Score
      })
      setLoading(true);
      console.log(response)
    });
    } catch (e) {
      console.log(e);
    }
  };

  useEffect(() => {
    apiCall();
  }, []);

  const theme = {
    blue: {
      default: "#3f51b5",
      hover: "#283593"
    },
    pink: {
      default: "#e91e63",
      hover: "#ad1457"
    }
  };

  const Button = styled.button`
    background-color: ${(props) => theme[props.theme].default};
    color: white;
    padding: 5px 15px;
    border-radius: 5px;
    outline: 0;
    text-transform: uppercase;
    margin: 10px 0px;
    cursor: pointer;
    box-shadow: 0px 2px 2px lightgray;
    transition: ease background-color 250ms;
    &:hover {
      background-color: ${(props) => theme[props.theme].hover};
    }
    &:disabled {
      cursor: default;
      opacity: 0.7;
    }
  `;


  const Tab = styled.button`
    padding: 10px 30px;
    cursor: pointer;
    opacity: 0.6;
    background: white;
    border: 0;
    outline: 0;
    border-bottom: 2px solid transparent;
    transition: ease border-bottom 250ms;
    ${({ active }) =>
      active &&
      `
      border-bottom: 2px solid black;
      opacity: 1;
    `}
  `;

  function Table(a) {
    return (
      <table>
            <thead>
              <tr>
                {/* {apiResponse.match_table[0].map((item, index) => {
                  return <th>{item}</th>;
                })} */}
                <th>Player 1</th>
                <th>Player 2</th>
                <th>Match Result</th>
                <th>Predicted Result</th>
              </tr>
            </thead>
            <tbody>
              {apiResponse && apiResponse.match_table && apiResponse.match_table.slice(1, apiResponse.match_table.length).map((item, index) => {
                return (
                  <tr>
                    <td>{item[0]}</td>
                    <td>{item[1]}</td>
                    <td>{item[2]}</td>
                    <td>{item[3]}</td>
                  </tr>
                );
              })}
            </tbody>
          </table>
    );
  }

  function TabGroup() {
    const [active, setActive] = useState(types[0]);
    return (
      <>
        <div>
          {types.map((type) => (
            <Tab
              key={type}
              active={active === type}
              onClick={() => {
                setActive(type)
              }}
            >
              {type}
            </Tab>
          ))}
        </div>
        {loading ? Table(): <ReactBootStrap.Spinner animation="border" />}
        {active}
      </>
    );
  }

  const types = ["Test", "Yesterday", "Today", "Tomorrow"];

  return (
    <div>

      <TabGroup />  
    </div>




      // {/* <p>Hello : <div>
      //               {apiResponse.match_table && apiResponse.match_table.map((item, index) => { return( <div key={index+1}>{item.value}</div> ) } ) }
      //            </div></p> */}

      // {/* {apiResponse ? (
      //   <div>
      //     <p>API response: {apiResponse.f1_score}</p>
      //     <p>{ apiResponse.match_table.map((item, index) => { return( <div key={index+1}>{item.value}</div> ) } ) }</p>
